import React, { useState } from 'react';


function Post (props) {

    const [like, setLike] = useState(false);

    const isLiked = props.post.likedBy.includes(props.userProps.id);

    return (
        <div className="post">
            <img src={props.post.picture} alt="post-pic" />
            <div>
                <p>{props.post.userName}</p>
                <p>{props.post.description}</p>
                {isLiked ? (
                    <span className="liked">{props.post.likes}</span>
                ) : (
                    <span className="likes">{props.post.likes}</span>
                )}
                
                <span className="comments">{props.post.comments.length}</span>
            </div>
            
        </div>
    );
}

export default Post;
