import React, { useState } from 'react';


function Post (props) {

    const [like, setLike] = useState(false);

    const isLiked = props.post.likedBy.includes(props.userProps.id);

    const likeFunction = async () => {
        await fetch('http://localhost:3000/posts/'+props.post.id, {
            method: 'PATCH',
            headers: { 
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body: JSON.stringify({
                likes: props.post.likes + 1,
                likedBy: [...props.post.likedBy, props.userProps.id]
            })
        });
    
    }

    return (
        <div className="post">
            <img src={props.post.picture} alt="post-pic" />
            <div>
                <p>{props.post.userName}</p>
                <p>{props.post.description}</p>
                {isLiked ? (
                    <span className="liked">{props.post.likes}</span>
                ) : (
                    <span className="likes" onClick={likeFunction}>{props.post.likes}</span>
                )}
                
                <span className="comments">{props.post.comments.length}</span>
            </div>
            
        </div>
    );
}

export default Post;
